import { useEffect } from 'react';
import socketIOClient from 'socket.io-client';
import './App.css';

const ENDPOINT = 'http://localhost:5000';

const App : React.FC = () => {

  useEffect(() : any => {
    const socket = socketIOClient(ENDPOINT);
    socket.on("chat message", data => {
      console.log(data)
    });

    // CLEAN UP THE EFFECT
    return () => socket.disconnect();
  }, []);

  const emmitMessage = () => {
    console.log("emmit")
    socket.emit('', {
      message: "jorge"
    });
  }

  return (
    <div className="App">
      <button onClick={emmitMessage}>Emitir</button>
    </div>
  );
}

export default App;
