import { useEffect, useState } from 'react';
import socketIOClient from 'socket.io-client';
import UserContext from './hooks/userContext';
import IUserData from './classes/IUserData';
import {
  BrowserRouter as Router,
  Switch,
} from "react-router-dom";
import './App.css';
import ChatView from './pages/ChatView';
import LoginView from './pages/LoginView';
import Authroute from './components/Authroute';
import UnauthRoute from './components/Unauthroute';

const ENDPOINT = process.env.REACT_APP_ENDPOINT || "http://localhost:5000";
const socket = socketIOClient(ENDPOINT);

const App: React.FC = () => {
  const [connected, setConnected] = useState<boolean>(false);
  const [userData, setUserData] = useState<IUserData>({
    room: "sala-1",
    name: ""
  });
  const [message, setMessage] = useState<string>("");
  const [messages, setMessages] = useState<Array<any>>([]);

  useEffect((): any => {
    socket.on("bot_message", data => {
      console.log(data);
      setMessages([...messages, data]);
    });
    socket.on("message", data => {
      console.log(data);
      setMessages([...messages, data]);
    });

    // CLEAN UP THE EFFECT
    return () => socket.disconnect();
  }, []);

  const emmitMessage = () => {
    socket.emit("sendMessage", {
      message,
      name: userData.name,
      room: userData.room
    });
    setMessage("");
  }

  const connect = (props : IUserData) => {
    socket.emit('join', props, (error: any) => {
      if(error) {
        alert(error);
        return;
      }
      setUserData(props);
      setConnected(true);
    });
  }

  return (
    <UserContext.Provider value={{ userData, connected, connect }}>
      <Router>
        <Switch>
          <UnauthRoute path={["/", "/login"]} exact>
              <LoginView />
          </UnauthRoute>
          <Authroute path="/chat" exact>
              <ChatView />
          </Authroute>
        </Switch>
      </Router>
    </UserContext.Provider>
  );
}

export default App;
